    [Fact]
    public void JwtToken_FromJwsInput_GetClaims_ReturnsValue()
    {
        var jwsValue = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJzdWIiOiIxMjM0NTY3ODkwIiwibmFtZSI6IkpvaG4gRG9lIiwiaWF0IjoxNTE2MjM5MDIyfQ.SflKxwRJSMeKKF2QT4fwpMeJf36POk6yJV_adQssw5c";

        var jwtToken = JwtToken.From(jwsValue);

        var claims = jwtToken.GetClaims();

        claims.Should().BeEquivalentTo(new[]
        {
            new Claim("sub", "1234567890", "http://www.w3.org/2001/XMLSchema#string"),
            new Claim("name", "John Doe", "http://www.w3.org/2001/XMLSchema#string"),
            new Claim("iat", "1516239022", "http://www.w3.org/2001/XMLSchema#integer"),
        });
    }

